[name=show_values].setChartOption:
function (v,self) {
          var value = isNaN(v) ? v : !!parseInt(v, 10);
          self.Chart.showLabels(value);
        }

[name=show_pointer].setChartOption:
function (v,self) {
          var value = !!parseInt(v, 10);
          self.Chart.showPointer(value);
