[name=controls].init:
function ($o) {
          this.initControl($o);

          $('[name=controls]').on('click', $.proxy(function(e) {
            //if icon clicked get parent button
            var button = $(e.currentTarget).data('control');
            switch (button) {
              case 'orientation':
                this.Chart.nodeSize({'width': 124, 'height': 56}).orientation();
                break;
              case 'show-all-nodes':
                this.Chart.showall();
                break;
              case 'zoom-to-fit':
                this.Chart.reset();
                break;
              default:
            }
          }, this));
        }
